import React, {useState} from 'react';

export default function StudentResult() {

  const [selectedSemester, setSelectedSemester] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedExamType, setSelectedExamType] = useState('');

  // Dummy data for semesters and subjects
  const semesters = ['sem1', 'sem2', 'sem3','sem4','sem5','sem6'];
  const subjects = {
    'sem1': ['HU101', 'SC102', 'SC103','SC104','EC105','ME106','ME107','EC108','SC109','CS110'],
    'sem2': ['HU201', 'SC202', 'SC203','SC204','CS205','ME206','ME207','CS208','SC209','CS210'],
    'sem3': ['SC301', 'EC302', 'CS303','CS304','CS305','CS306','CS307','EC308','CS309','HU310'],
    'sem4': ['SC401', 'CS402', 'CS403','CS404','CS405','CS406','CS407','CS408','CS409','HU410'],
    'sem5': ['ME501', 'CS502', 'CS503','CS584','CS585','CS506','CS507','CS588','CS509','CS510'],
    'sem6': ['CS601'],
  };

  // Event handlers
  const handleSemesterChange = (event) => {
    const selectedSemester = event.target.value;
    setSelectedSemester(selectedSemester);
    // Reset selected subject when semester changes
    setSelectedSubject('');
  };

  const handleSubjectChange = (event) => {
    const selectedSubject = event.target.value;
    setSelectedSubject(selectedSubject);
  };

  const examTypeChange = (event) => {
    const selectedExamType = event.target.value;
    setSelectedExamType(selectedExamType);
  };

  return (
    <div className='result-container'>
      <div className="row">
      <div className="col-md-3">
      <label htmlFor="semester">Select Semester:</label>
            <select className='result-dropdowns' id="semester" value={selectedSemester} onChange={handleSemesterChange}>
              <option value="">Select</option>
              {semesters.map((semester) => (
                <option key={semester} value={semester}>
                  {semester}
                </option>
              ))}
            </select>
      </div>
      <div className="col-md-3">
            {/* Subject dropdown */}
            {selectedSemester && (
              <>
                <label htmlFor="subject">Select Subject:</label>
                <select className='result-dropdowns' id="subject" value={selectedSubject} onChange={handleSubjectChange}>
                  <option value="">Select</option>
                  {subjects[selectedSemester].map((subject) => (
                    <option key={subject} value={subject}>
                      {subject}
                    </option>
                  ))}
                </select>
              </>
            )}
      </div>
      <div className="col-md-3">
            <label htmlFor="ExamType">Exam Type:</label><br />
            <select className='result-dropdowns' id="ExamType" value={selectedExamType} onChange={examTypeChange}>
                <option value="">Select</option>
                <option value="MID1" name="MID1" key="MID1">MID1</option>
                <option value="MID2" name="MID2">MID2</option>
                <option value="ENDSEM" name="ENDSEM">ENDSEM</option>
            </select>
      </div>      
      </div>
    </div>
  )
}
