import React, { useState } from 'react';

export default function ImageAndInfoContainer(props) {
    const [isHovered, setIsHovered] = useState(false);

    const containerStyle = {
        padding: '20px',           // Add padding for better visual appeal
        textAlign: 'center',       // Align text to the center
        margin: '20px ',
        fontFamily: 'Arial, sans-serif',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: 'white',
        borderRadius: '8px'
    };
    const imageStyle = {
        height: '100px',  // Set a fixed height
        width: '100px',  // Maintain aspect ratio and cover the container
    };
  return (
    <div style={containerStyle}>
    <h1>{props.title}</h1> <hr />
    <img src={props.image} alt='someImage' style={imageStyle} />
    <p>{props.info}</p>
    </div>
  )
}
