import React from 'react';
import Calendar from 'react-calendar';

const EventsPage = ({ events, onDateClick, selectedDate, onDeleteEvent }) => {
  return (
    <div>
      <Calendar
        onChange={onDateClick}
        value={selectedDate}
        tileContent={({ date, view }) => {
          const dateStr = date.toDateString();
          const eventsForDate = events.filter(event => event.date === dateStr);
          
          return eventsForDate.map((event, index) => (
            <div key={index}>
              {event.title} <br></br>
              <button onClick={() => onDeleteEvent(event.id)}>Delete</button>
            </div>

          ));
        }}
      />
    </div>
  );
};

export default EventsPage;
