import React, { useState } from 'react';
import PropTypes from 'prop-types';

export default function TextContainer(props) {
    const [isExpanded, setIsExpanded] = useState(false);

    const containerStyle = {
        padding: '20px',
        textAlign: 'left',
        // margin: '20px',
        fontFamily: 'Arial, sans-serif',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: 'white',
        borderRadius: '8px'
    };

    const toggleExpansion = () => {
        setIsExpanded(!isExpanded);
    };

    // Convert props.info to a string if it's an object
    const infoString = typeof props.info === 'object' ? JSON.stringify(props.info) : props.info;

    const truncatedInfo = `${props.info.toString().slice(0, 60)}${props.info.length > 60 ? '...' : ''}`;

    return (
        <div style={containerStyle}>
            <h1 style={{ color: `10px solid ${props.headingColor || 'green'}` }}>{props.title}</h1> <hr />
            <p>
                {isExpanded ? infoString : truncatedInfo}
                {infoString.length > 60 && (
                    <button className="btn btn-link" onClick={toggleExpansion}>
                        {isExpanded ? 'Read Less' : 'Read More'}
                    </button>
                )}
            </p>
        </div>
    );
}

TextContainer.propTypes = {
    title: PropTypes.string.isRequired,
    info: PropTypes.string.isRequired,
    headingColor: PropTypes.string,
    // ... other prop types
};
