// AdminCalendar.js
import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import EventModal from './EventModal';

const EventManagement = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [isModalOpen, setModalOpen] = useState(false);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch('/api/events');
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleEventSave = async (eventName, eventDescription) => {
    try {
      const response = await fetch('/api/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          date: selectedDate.toDateString(),
          eventName,
          eventDescription,
        }),
      });

      if (response.ok) {
        console.log('Event saved successfully!');
        setModalOpen(false);
        fetchEvents();
      } else {
        console.error('Error saving event:', response.statusText);
      }
    } catch (error) {
      console.error('Error saving event:', error);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    try {
      const response = await fetch(`/api/events/${eventId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        console.log('Event deleted successfully!');
        fetchEvents();
      } else {
        console.error('Error deleting event:', response.statusText);
      }
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const tileContent = ({ date }) => {
    const eventsForDate = events.filter((event) => event.date === date.toDateString());

    if (eventsForDate.length > 0) {
      return <p className="event-date">{eventsForDate.length}</p>;
    }

    return null;
  };

  return (
    <div className="admin-calendar-container">
      <Calendar
        onClickDay={handleDateClick}
        value={selectedDate}
        tileContent={tileContent}
        className="admin-calendar"
      />
      {isModalOpen && (
        <EventModal
          date={selectedDate}
          existingEvent={events.find((event) => event.date === selectedDate.toDateString())}
          closeModal={() => setModalOpen(false)}
          handleEventSave={(eventName, eventDescription) =>
            handleEventSave(eventName, eventDescription)
          }
          handleDeleteEvent={(eventId) => handleDeleteEvent(eventId)}
        />
      )}
    </div>
  );
};

export default EventManagement;
