// EventModal.js
import React, { useState, useEffect } from 'react';

const EventModal = ({ date, closeModal, handleEventSave }) => {
  const [eventText, setEventText] = useState('');
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const eventsForDate = storedEvents.filter((event) => event.date === date.toDateString());
    setEvents(eventsForDate);
  }, [date]);

  const handleSaveEvent = () => {
    const newEvent = { date: date.toDateString(), text: eventText };
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const updatedEvents = [...storedEvents, newEvent];
    localStorage.setItem('events', JSON.stringify(updatedEvents));
    handleEventSave();
    setEventText('');
  };

  const handleDeleteEvent = (index) => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const updatedEvents = storedEvents.filter((event, i) => i !== index);
    localStorage.setItem('events', JSON.stringify(updatedEvents));
    handleEventSave();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={closeModal}>&times;</span>
        <h3>Add Event for {date.toDateString()}</h3>
        <textarea
          value={eventText}
          onChange={(e) => setEventText(e.target.value)}
          placeholder="Enter event description"
        />
        <button onClick={handleSaveEvent}>Save Event</button>

        <div className="event-list">
          <h4>Events for {date.toDateString()}</h4>
          {events.length === 0 ? (
            <p>No events for this date.</p>
          ) : (
            <ul>
              {events.map((event, index) => (
                <li key={index}>
                  <span>{event.text}</span>
                  <button onClick={() => handleDeleteEvent(index)}>Delete</button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default EventModal;
