import React, { useState } from "react";
import collegeImage2 from "./CollegeImage2.jpg";
import collegePhoto from "./CollegePhoto.JPG";
import collegeImage5 from "./CollegeImage5.jpg";
import TextContainer from "./textContainer";
import IMAGE from "./IMAGE.png";
import ImageAndInfoContainer from "./ImageAndInfoContainer";

export default function HomePage() {
  const rowStyle = {
    display: "flex",
    alignItems: "stretch",
    marginBottom: "20px",
  };

  const imageContainerStyle = {
    border: "10px solid hsl(210, 100%, 80%)",
    margin: "20px",
    height: "600px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
  };

  const imageStyle = {
    width: "100%",
    height: "auto",
    objectFit: "contain",
  };

  const infoStyle = {
    marginBottom: "20px",
  };

  const [isExpanded, setIsExpanded] = useState(false);

  const handleExpand = () => {
    setIsExpanded(true);
  };

  const truncateWords = (text, limit) => {
    const words = text.split(" ");
    if (words.length > limit) {
      return words.slice(0, limit).join(" ") + " ...";
    }
    return text;
  };

  const infoText = `This institution is one of the oldest polytechnics in the state, was established in 1923 with two branches Electrical and Mechanical. Originally named as Osmania Central Technical College and located in the Mint Compound at Masab Tank. In 1931, it was renamed as Osmania Technical College. After India gained independence in 1947, the institution's name was changed to Government Polytechnic Hyderabad in 1954 and technical courses were introduced with two branches of study: Civil, and Automobile. In 1957, a course in the branch of telecommunications (electronics) was added. The institution was shifted to the present campus at Masab Tank in the academic year 1960–61. Subsequently, a diploma in pharmacy was introduced in 1971–72. A diploma in computers was introduced in 1986–87.`;

  return (
    <div>
      <div className="Main-container" style={{ backgroundColor: '#f4f4f4', padding: '20px' }}>
        {/* First Row */}
        <div className="row" style={rowStyle}>
          <div className="col-md-8">
            <div id="carouselExampleIndicators" className="carousel slide" style={imageContainerStyle}>
              <div className="carousel-indicators">
                <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="0" className="active" aria-current="true" aria-label="Slide 1"></button>
                <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="1" aria-label="Slide 2"></button>
                <button type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide-to="2" aria-label="Slide 3"></button>
              </div>
              <div className="carousel-inner">
                <div className="carousel-item active">
                  <img src={collegeImage2} className="d-block w-100" alt="collegeImage1" style={imageStyle} />
                </div>
                <div className="carousel-item">
                  <img src={collegePhoto} className="d-block w-100" alt="collegeImage2" style={imageStyle} />
                </div>
                <div className="carousel-item">
                  <img src={collegeImage5} className="d-block w-100" alt="collegeImage3" style={imageStyle} />
                </div>
              </div>
              <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                <span className="visually-hidden">Previous</span>
              </button>
              <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                <span className="visually-hidden">Next</span>
              </button>
            </div>
          </div>
          <div className="col-md-4">
            <ImageAndInfoContainer
              borderColor="hsl(203, 92%, 75%)"
              title="Principal"
              image={IMAGE}
              info={
                <div style={infoStyle}>
                  <p>
                    <b>Dr. Rajeshwari Devi</b>
                    <br />
                    {isExpanded ? infoText : truncateWords(infoText, 30)}
                  </p>
                  {!isExpanded && (
                    <button className="btn btn-link" onClick={handleExpand}>
                      Read More
                    </button>
                  )}
                </div>
              }
            />
          </div>
        </div>

        {/* Second Row */}
        <div className="row" style={rowStyle}>
          <div className="col-md-6">
            <TextContainer
              borderColor="hsl(208, 100%, 90%)"
              title="About Us"
              info={truncateWords(infoText, 50)}
            />
          </div>
          <div className="col-md-6">
            <TextContainer
              borderColor="hsl(203, 92%, 75%)"
              title="Facilities"
              info={
                <div style={infoStyle}>
                  <p>
                    <b>Library</b>- The institute has a library with a huge collection
                    of books, periodicals, journals, CDs, non-book materials and
                    magazines. The library is well equipped with modern facilities and
                    resources in the forms one CDs, DVDs, E-journals, patents etc
                    apart from the usual collection of books.
                    <hr />
                    <b>Hostels</b>- There is a boy hostel beside the college with
                    capacity for 440 and a girl hostel built internally in the college
                    with capacity for 80. All hostels are well built, clean and
                    provide hygienic food. Besides hostels have round the clock
                    electric and water supply.
                    <hr />
                    <b>Laboratories</b>- Laboratories are maintained and monitored by
                    laboratory assistants. Besides the institute has a computer centre
                    equipped with 25 desktop computers with 10 KVA-UPS, Printer, DLP,
                    Design software, etc. Besides the institute has Mechanical
                    Workshop, Engineering Workshop to teach students and help them
                    gain hands-on training in Mechanical engineering.
                    <hr />
                    <b>Health Centre</b>- To cope up with everyday life stress and
                    life style diseases, the institute offers a health centre equipped
                    with modern equipment like Tread mill, Manual Tread Mill, Weighing
                    Machine, Gym ball etc.
                    <hr />
                    <b>Sports Facility</b>- The institute has both indoor and outdoor
                    sports facility to nurture the idea of overall well-being of
                    students both mentally and physically. The institute has football
                    grounds, basketball courts, badminton courts where students
                    practice regularly. Besides the institute arranges Annual Sports
                    Day every year where students and faculties take active
                    participation.
                  </p>
                  {!isExpanded && (
                    <button className="btn btn-link" onClick={handleExpand}>
                      Read More
                    </button>
                  )}
                </div>
              }
            />
          </div>
        </div>

        {/* Third Row */}
        <div className="row" style={rowStyle}>
          <div className="col-md-6">
            <TextContainer
              borderColor="hsl(203, 92%, 75%)"
              title="Vision"
              info={truncateWords(infoText, 30)}
            />
          </div>
          <div className="col-md-6">
            <TextContainer
              borderColor="hsl(208, 100%, 90%)"
              title="Mission"
              info={truncateWords(infoText, 30)}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
