import React, { useState } from 'react';
import PropTypes from 'prop-types';

export default function ImageAndInfoContainer(props) {
  const [isExpanded, setIsExpanded] = useState(false);

    const containerStyle = {
        padding: '20px',           // Add padding for better visual appeal
        textAlign: 'center',       // Align text to the center
        fontFamily: 'Arial, sans-serif',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        backgroundColor: 'white',
        borderRadius: '8px'
    };

    const toggleExpansion = () => {
      setIsExpanded(!isExpanded);
    };

    const truncatedInfo = `${props.info.toString().slice(0, 60)}${props.info.length > 60 ? '...' : ''}`;

    const imageStyle = {
        height: '100px',  // Set a fixed height
        width: '100px',  // Maintain aspect ratio and cover the container
    };
  return (
    <div style={containerStyle}>
    <h1 style={{ color: `10px solid ${props.headingColor || 'green'}` }}>{props.title}</h1> <hr />
    <img src={props.image} alt='someImage' style={imageStyle} />
    <p>{isExpanded ? props.info : truncatedInfo}
                {props.info.length > 60 && (
                    <button className="btn btn-link" onClick={toggleExpansion}>
                        {isExpanded ? 'Read Less' : 'Read More'}
                    </button>
                )}
    </p>
    </div>
  )
}

ImageAndInfoContainer.propTypes = {
  title: PropTypes.string.isRequired,
  info: PropTypes.string.isRequired,
  headingColor: PropTypes.string,
  // ... other prop types
};
