// EventModal.js
import React, { useState, useEffect } from 'react';


const EventModal = ({ date, existingEvent, closeModal, handleEventSave, handleDeleteEvent }) => {
  const [eventText, setEventText] = useState('');
  const [currentEvent, setCurrentEvent] = useState(existingEvent);

  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const existingEventForDate = storedEvents.find((event) => event.date === date.toDateString());

    if (existingEventForDate) {
      setCurrentEvent(existingEventForDate);
      setEventText(existingEventForDate.text);
    } else {
      setCurrentEvent(null);
      setEventText('');
    }
  }, [date]);

  const handleSaveEvent = () => {
    const newEvent = { date: date.toDateString(), text: eventText };

    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const updatedEvents = storedEvents.filter((event) => event.date !== date.toDateString());
    localStorage.setItem('events', JSON.stringify([...updatedEvents, newEvent]));

    handleEventSave();
    setEventText('');
    setCurrentEvent(newEvent);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={closeModal}>&times;</span>
        <h3>{currentEvent ? 'Edit Event' : 'Add Event'} for {date.toDateString()}</h3>
        <textarea
          value={eventText}
          onChange={(e) => setEventText(e.target.value)}
          placeholder="Enter event description"
        />
        <button onClick={handleSaveEvent}>{currentEvent ? 'Update Event' : 'Save Event'}</button>
        {currentEvent && (
          <div>
            <button onClick={handleDeleteEvent} className="delete-event-btn">Delete Event</button>
            <button onClick={() => setCurrentEvent(null)}>Add Another Event</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventModal;
