// EventManagement.js
import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import EventModal from './EventModal';

const EventManagement = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [isModalOpen, setModalOpen] = useState(false);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    setEvents(storedEvents);
  }, [isModalOpen]);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setModalOpen(true);
  };

  const handleEventSave = () => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const newEvent = { date: selectedDate.toDateString(), text: 'Your text here' }; // Replace with actual text
    const updatedEvents = [...storedEvents, newEvent];
    localStorage.setItem('events', JSON.stringify(updatedEvents));
    setEvents(updatedEvents);
    setModalOpen(false);
  };
  
  const handleDeleteEvent = () => {
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const updatedEvents = storedEvents.filter((event) => event.date !== selectedDate.toDateString());
    localStorage.setItem('events', JSON.stringify(updatedEvents));
    setEvents(updatedEvents);
    setModalOpen(false);
  };

  const tileContent = ({ date, view }) => {
    const eventsForDate = events.filter((event) => event.date === date.toDateString());

    if (view === 'month' && eventsForDate.length > 0) {
      return <p className="event-date">{eventsForDate.length}</p>;
    }

    return null;
  };

  return (
    <div className="custom-calendar-container">
      <Calendar
        onClickDay={handleDateClick}
        value={selectedDate}
        tileContent={tileContent}
        className="custom-calendar"
      />
      {isModalOpen && (
        <EventModal
          date={selectedDate}
          existingEvent={events.find((event) => event.date === selectedDate.toDateString())}
          closeModal={() => setModalOpen(false)}
          handleEventSave={handleEventSave}
          handleDeleteEvent={handleDeleteEvent}
        />
      )}
      <div className="event-list">
  <h4>Events for {selectedDate.toDateString()}</h4>
  {events
    .filter((event) => event.date === selectedDate.toDateString())
    .map((event) => (
      <div key={event.date}>
        <ul>
          <li>{event.text}</li>
        </ul>
      </div>
    ))}
</div>
    </div>
  );
};

export default EventManagement;
