import React, { useState } from 'react';
import StudentAttendance from './StudentAttendance';
import StudentResult from './StudentResult';

export default function PlacementsPage() {
    const [currentPage, setCurrentPage] = useState('StudentAttendance');
  
    const handleMenuClick = (page) => {
      setCurrentPage(page);
    };
  
    return (
        <div className="placements-main-container row">
          {/* Left Column */}
          <div className="col-md-8">
            {currentPage === 'StudentAttendance' && <StudentAttendance />}
            {currentPage === 'StudentResult' && <StudentResult />}
          </div>
  
          {/* Right Column - Menus */}
          <div className="placement-menus col-md-3">
            <h2 style={{color:'hsl(240, 70%, 28%)'}}>Student Attendance and Results</h2>
            <hr style={{color:'hsl(240, 50%, 50%)'}} />
            <ul className=" list-group">
              <li className="menuItem list-group-item" onClick={() => handleMenuClick('StudentAttendance')}>
                Student Attendance
              </li>
              <li className="menuItem list-group-item" onClick={() => handleMenuClick('StudentResult')}>
                Student Result
              </li>
            </ul>
          </div>
        </div>
    );
  };
