import React from 'react'

export default function textContainer(props) {
    const containerStyle = {
        border: '10px solid green',  // Add a border to the container
        padding: '20px',           // Add padding for better visual appeal
        textAlign: 'left',       // Align text to the center
        width: '70%',
        textJustify: 'inter-word',
        margin: '20px auto',
        fontFamily: 'Arial, sans-serif',
    };
    const imageStyle = {
        height: '100px',  // Set a fixed height
        width: '100px',  // Maintain aspect ratio and cover the container
    };
  return (
    <div style={containerStyle}>
    <h1>{props.title}</h1> <hr />
    <div style={imageStyle}>
        <img src={props.image} alt="image" />
    </div>
    <p>{props.info}</p>
    </div>
  )
}
