import React from 'react'

export default function textContainer(props) {
    const [isHovered, setIsHovered] = useState(false);

    const containerStyle = {
        border: `10px solid ${props.borderColor || 'green'}`,  // Add a border to the container
        padding: '20px',           // Add padding for better visual appeal
        textAlign: 'center',       // Align text to the center
        width: isHovered ? '75%' : '70%',
        textJustify: 'inter-word',
        margin: '20px auto',
        fontFamily: 'Arial, sans-serif',
    };
    const imageStyle = {
        height: '100px',  // Set a fixed height
        width: '100px',  // Maintain aspect ratio and cover the container
    };
  return (
    <div style={containerStyle}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
    >
    <h1>{props.title}</h1> <hr />
    <img src={props.image} alt='someImage' style={imageStyle} />
    <p>{props.info}</p>
    </div>
  )
}
