import React from 'react';
import Calendar from 'react-calendar';
import { Link } from 'react-router-dom';

const EventCalendar = ({ events, onDateClick, selectedDate, onDeleteEvent }) => {
  return (
    <div>
      <Calendar
        onChange={onDateClick}
        value={selectedDate}
        tileContent={({ date, view }) => {
          const dateStr = date.toDateString();
          const eventsForDate = events.filter(event => event.date === dateStr);
          
          return eventsForDate.map((event, index) => (
            <div key={index}>
              {event.title} <br></br>
              <button onClick={() => onDeleteEvent(event.id)}>Delete</button>
            </div>

          ));
        }}
      />
      <Link to="/events">Add event</Link>
    </div>
  );
};

export default EventCalendar;
