import React from 'react'

export default function textContainer(props) {
const textStyle={
    border: `10px solid ${props.BorderColor || 'green'}`,
    padding: '35px',
    margin: '20px',
    color: 'hsl(240, 70%, 35%)',
    backgroundColor: 'white',
    borderRadius: "5px",
    position:'relative'
}

const imageContainer={
  
    position: 'absolute',
    // top: '-50%',  /* Adjust as needed to align with border */
    transform: 'translateX(-50%)',  /* Center horizontally */
    width: "12vh" /* Adjust the size of the circular image */,
    height: "12vh",
    borderRadius: "50%" /* Creates a circular clipping mask */,
    /* overflow: "hidden" */ /* Ensures the image stays within the circular mask */
    top:'-40px',
    zIndex:'1'

  }

  return (
    <div style={textStyle}>
      <img src={props.ContainerImage} alt="titleImage" style={imageContainer} />
      <h4 style={{color:'black',textAlign:'center',fontFamily:'times new roman',fontWeight:'bold'}}>{props.title}</h4>
      <marquee behavior="scroll" direction="up" scrollamount="2"><b>{props.ContainerData}</b></marquee>
    </div>
  )
}
