// EventModal.js
import React, { useState } from 'react';

const EventModal = ({ date, closeModal }) => {
  const [eventText, setEventText] = useState('');

  const handleSaveEvent = () => {
    // Handle saving the event, for example, by calling an API or updating local storage
    console.log(`Event saved for ${date.toDateString()}: ${eventText}`);
    
    // Close the modal
    closeModal();
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={closeModal}>&times;</span>
        <h3>Add Event for {date.toDateString()}</h3>
        <textarea value={eventText} onChange={(e) => setEventText(e.target.value)} />
        <button onClick={handleSaveEvent}>Save Event</button>
      </div>
    </div>
  );
};

export default EventModal;
