import React, { useState } from 'react';

export default function PlacementsPage() {
    const [currentPage, setCurrentPage] = useState('PlacementHome');
  
    const handleMenuClick = (page) => {
      setCurrentPage(page);
    };
  
    return (
        <div className="row">
          {/* Left Column */}
          <div className="col-md-9">
            {currentPage === 'PlacementHome' && <PlacementHome />}
            {currentPage === 'DiplomaPrograms' && <DiplomaPrograms />}
            {currentPage === 'MajorRecruiters' && <MajorRecruiters />}
            {currentPage === 'RecruitersGuide' && <RecruitersGuide />}
            {currentPage === 'StudentsGuide' && <StudentsGuide />}
            {currentPage === 'Contactus' && <Contactus />}
          </div>
  
          {/* Right Column - Menus */}
          <div className="col-md-3 bg-light">
            <ul className="list-group">
              <li className="list-group-item" onClick={() => handleMenuClick('PlacementHome')}>
                Placement Officer
              </li>
              <li className="list-group-item" onClick={() => handleMenuClick('DiplomaPrograms')}>
                Diploma Programs
              </li>
              <li className="list-group-item" onClick={() => handleMenuClick('MajorReruiters')}>
                Major Recruiters
              </li>
              <li className="list-group-item" onClick={() => handleMenuClick('RecruitersGuide')}>
                For Recruiters
              </li>
              <li className="list-group-item" onClick={() => handleMenuClick('StudentsGuide')}>
                For Students
              </li>
              <li className="list-group-item" onClick={() => handleMenuClick('Contactus')}>
                Contact Us
              </li>
            </ul>
          </div>
        </div>
    );
  };
