// FacultyDashboard.jsx

import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import AttendanceInput from './AttendanceInput.jsx';
import AttendanceTable from './AttendanceTable.jsx';

export default function FacultyDashboard() {
  const [showStudentOptions, setShowStudentOptions] = useState(false);

  // Define your export function here
  const yourExportFunction = (data) => {
    // Do something with the exported data
    console.log('Exported data:', data);
  };

  const handlemenuclick = (page) => {
    if (page === 'AttendanceManagement') {
      setShowStudentOptions((prev) => !prev);
    }
  };

  return (
    <div className="placements-main-container row">
      {/* Left Column */}
      <div className="col-md-8">
        <Routes>
          <Route
            path="attendanceInput"
            element={<AttendanceInput onExport={yourExportFunction} />}
          />
          <Route
            path="attendanceTable"
            element={<AttendanceTable showStudentOptions={showStudentOptions} />}
          />
        </Routes>
      </div>

      {/* Right Column - Menus */}
      <div className="placement-menus col-md-3">
        <h2 style={{ color: 'hsl(240, 70%, 28%)' }}>Student Attendance</h2>
        <hr style={{ color: 'hsl(240, 50%, 50%)' }} />
        <ul className=" list-group">
          <li
            className="menuItem list-group-item"
            onClick={() => {
              handlemenuclick('AttendanceManagement');
            }}
          >
            Take Student Attendance
          </li>
          {showStudentOptions && (
            <>
              <li
                className="menuItem list-group-item"
                onClick={() => {
                  // Navigate to AttendanceInput component
                  navigate('/attendanceInput');
                }}
              >
                Attendance Input
              </li>
              <li
                className="menuItem list-group-item"
                onClick={() => {
                  // Navigate to AttendanceTable component
                  navigate('/attendanceTable');
                }}
              >
                Attendance Table
              </li>
            </>
          )}
        </ul>
      </div>
    </div>
  );
}
