import React, {useState} from 'react';

export default function StudentAttendance() {

  const [selectedSemester, setSelectedSemester] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('');

  const semesters = ['sem1', 'sem2', 'sem3','sem4','sem5','sem6'];
  const departments = ['Automobile Engineering','Civil Engineering','Computer Engineering','Electrical and Electronics Engineering','Electronics and Communication Engineering','Pharmaceutical Engineering'];

  const handleSemesterChange = (event) => {
    const selectedSemester = event.target.value;
    setSelectedSemester(selectedSemester);
  };

  const handleDepartmentChange = (event) => {
    const selectedDepartment = event.target.value;
    setSelectedDepartment(selectedDepartment);
  };

  return (
    <div className='student-attendance-container'>
      <div className="row">
        <div className="col-md-5">
            <label htmlFor="department">Select Department:</label>
            <select className='result-dropdowns' id="department" value={selectedDepartment} onChange={handleDepartmentChange}>
                <option value="">Select</option>
                {departments.map((department) => (
                <option key={department} value={department}>
                  {department}
                </option>
              ))}
            </select>
        </div>
        <div className="col-md-5">
            <label htmlFor="semester">Select Semester:</label>
            <select className='result-dropdowns' id="semester" value={selectedSemester} onChange={handleSemesterChange}>
              <option value="">Select</option>
              {semesters.map((semester) => (
                <option key={semester} value={semester}>
                  {semester}
                </option>
              ))}
            </select>
        </div>
      </div>
</div>
  )
}
