import React, { useState, useEffect } from 'react';
import axios from 'axios';

const InputForm = ({ onExport }) => {
  const [departments, setDepartments] = useState([]);
  const [semesterOptions] = useState([1, 2, 3, 4, 5, 6]);
  const [department, setDepartment] = useState('');
  const [semester, setSemester] = useState('');
  const [subject, setSubject] = useState('');
  const [attendanceResults, setAttendanceResults] = useState(null);

  useEffect(() => {
    // Fetch departments from the server using Axios
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/departments'); // Update with your API endpoint
      setDepartments(response.data);
    } catch (error) {
      console.error('Error fetching departments:', error);
    }
  };

  // const handleKeyPress = (e) => {
  //   if (e.key === 'Enter') {
  //     submitInput();
  //   }
  // };

  const submitInput = () => {
    // Check if all required fields are filled before exporting
    if (department && semester && subject) {
      // Call the onExport function with the export data
      onExport({ department, semester, subject });
      
      // Example: Call the API endpoint to mark attendance
      axios.post('http://localhost:3000/api/student_details', { params: { department, semester ,subject} })
      .then(response => {
        // Handle the response if needed
        console.log('Student details:', response.data);
      })
      .catch(error => {
        // Handle errors
        console.error('Error fetching student details:', error);
      });
    } else {
      alert('Please fill in all the fields');
    }
  };

  return (
    <div style={{ margin: '20px', fontSize: '19px' }}>
      <label>
        Department:
        <select
  value={department}
  onChange={(e) => setDepartment(e.target.value)}
  required
  style={{ margin: '20px', fontSize: '20px' }}
>
  <option value="" disabled>Select Department</option>
  {departments.map((dept) => (
    <option key={dept.D_id} value={dept.D_id}>
      {dept.D_name}
    </option>
  ))}
</select>

      </label>
      <br />
      <label>
        Semester:
        <select
          value={semester}
          onChange={(e) => setSemester(e.target.value)}
          required
          style={{ margin: '20px', fontSize: '20px' }}
        >
          <option value="" disabled>Select Semester</option>
          {semesterOptions.map((sem) => (
            <option key={sem} value={sem}>
              {sem}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Subject:
        <input
          type="text"
          value={subject}
          onInput={(e) => {
            const inputValue = e.target.value;
            if (/^[a-zA-Z0-9_]+$/.test(inputValue)) {
              setSubject(inputValue);
            }
          }}
          pattern="[a-zA-Z0-9_]+"
          title="Only letters, numbers, and underscores are allowed"
          required
          style={{ margin: '20px', fontSize: '20px' }}
        />

      </label>
      <br />
      <button onClick={submitInput} style={{ margin: '20px', fontSize: '20px' }}>Mark Attendance</button>
      
      {attendanceResults && (
        <div>
          <h2>Attendance Results</h2>
          <p>Department: {attendanceResults.department}</p>
          <p>Semester: {attendanceResults.semester}</p>
          <p>Subject: {attendanceResults.subject}</p>
          {/* Display additional information as needed */}
        </div>
      )}
    </div>
  );
};

export default InputForm;
